typedef typename FilterType::NodeType      NodeType;
typedef typename FilterType::NodeContainer NodeContainerType;

typename NodeContainerType::Pointer seed1Nodes = NodeContainerType::New();
seed1Nodes->reserve( m_SeedPoints1.size() );

for (unsigned int i = 0; i < m_SeedPoints1.size(); ++i)
  {
  NodeType node;

  typename NodeType::IndexType index = sitkSTLVectorToITK<typename NodeType::IndexType>( m_SeedPoints1[i] );
  node.SetIndex( index );
  node.SetValue( 0.0 );
  if (m_SeedPoints1[i].size() > NodeType::SetDimension)
    node.SetValue(m_SeedPoints1[i][NodeType::SetDimension]);

  seed1Nodes->push_back( node );
  }
filter->SetSeedPoints1( seed1Nodes );

