members.custom_itk_cast:
itk::ConstantBoundaryCondition< InputImageType > cbc;
  itk::ZeroFluxNeumannBoundaryCondition< InputImageType > zbc;
  itk::PeriodicBoundaryCondition< InputImageType > pbc;
  switch ( m_BoundaryCondition )
    {
    case ZERO_PAD:
    {
    filter->SetBoundaryCondition( &cbc );
    }
    break;

    case ZERO_FLUX_NEUMANN_PAD:
    {
    filter->SetBoundaryCondition( &zbc );
    }
    break;

    case PERIODIC_PAD:
    {
    filter->SetBoundaryCondition( &pbc );
    }
    break;
 }
